mapper.update:
UPDATE pet
SET petname   = #{pet.petName},
    petbreed  = #{pet.petBreed},
    birthdate = #{pet.birthDate},
    pettypeid = #{pet.petTypeId},
    pfile     = #{pet.pfile}
WHERE petid   = #{pet.petId}
  AND userid  = #{userId}

UPDATE pet
SET petname   = #{petName}
WHERE petid   = #{petId}

mapper.delete:
DELETE FROM pet
WHERE petid = #{petId}
  AND userid = #{userId}
