mapper.insert:
INSERT INTO pets (
    petid, userid, petname, petbreed, birthdate,
    pettypeid, createdat, pfile, page, pgender
)
VALUES (
    pet_seq.NEXTVAL, #{userId}, #{petName}, #{petBreed},
    #{birthDate}, #{petTypeId}, SYSDATE, #{pFile, jdbcType=VARCHAR},
    #{pAge}, #{pGender}
)

mapper.update:
UPDATE pets
SET petname   = #{pet.petName},
    petbreed  = #{pet.petBreed},
    birthdate = #{pet.birthDate},
    pettypeid = #{pet.petTypeId},
    pfile     = #{pet.pFile},
    page      = #{pet.pAge},
    pgender   = #{pet.pGender}
WHERE petid   = #{pet.petId}
  AND userid  = #{userId}
